	function moonPhase($year, $month, $day) {
		    /*k
		      Calculates the moon phase (0-7), accurate to 1 segment.
		      0 = > new moon.
		      4 => Full moon.
		    */
		   
		    if ($month == 1) {
		    	--$day;
	    	} elseif ($month == 2) {
	    		$day += 30;
	    	} else {
		        $day += 28 + ($month - 2) * 3059 / 100;
		 
		        // adjust for leap $years
		        if (!($year & 3)) ++$day;
		        if (($year%100) == 0) --$day;
		    }
		   
		    $g = ($year-1900) % 19 + 1;
		    $e = (11 * $g + 18) % 30;
		    
		    if (($e == 25 && $g > 11) || $e == 24) {
		    	$e++;
		    }
		    
		    return (((($e + $day) * 6 + 11) % 177) / 22 & 7);
	}
	
	
	function getMoonIcon($phase) {
		
		if ($phase >= 1 && $phase <= 3) {
			return array(
				0 => 'svg-moon-up',
				1 => 'Растущая'
			);
		}
		
		if ($phase == 4) {
			return array(
				0 => 'svg-moon-full',
				1 => 'Полнолуние'
			);
		}
		
		if ($phase == 5 || $phase == 6) {
			return array(
				0 => 'svg-moon-down',
				1 => 'Убывающая'
			);
		}
		
		if ($phase == 7 || $phase == 0) {
			return array(
				0 => 'svg-moon-young',
				1 => 'Новолуние'
			);
		}
	}
	
